import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import axios from 'axios';

import Posts from './components/Posts';
import Pagination from './components/Pagination';

import './App.css';

const App = () => {
  const [posts, setPosts] = useState([]);
  const[offset, setOffset]=useState(0);
  const[perPage, setPerPage]=useState(10);
  const [currentPage, setCurrentPage] = useState(0);
  const [pageCount, setPageCount] = useState('');
  const [isReceive, setIsReceive] = useState(false);

    useEffect(() => {
      const fetchPosts = async () => {
        await axios.get(`https://api.punkapi.com/v2/beers`)
        .then((res)=>{
          setPosts(res.data);
          setIsReceive(true)
        })
      };
      fetchPosts();
    }, []);

    useEffect(()=>{
      receiveData()
    },[isReceive === true])

    useEffect(()=>{
      setIsReceive(false);
      receiveData()
    },[offset,currentPage])

   console.log(posts && posts)



  const receiveData=()=>{
    var data =[]
    data= posts;
    console.log(data,"=====rec",posts)
    const sliceData =data && data.slice(offset,offset + perPage)
    console.log(sliceData,"====sliceData")
    const pageCount = Math.ceil(data && data.length/perPage)
    console.log(pageCount,"===pageCount")
    setPosts(sliceData)
    setPageCount(pageCount)
  }

  const handlePageClick=(e)=>{
    const selectedPage= e.selected;
    console.log(selectedPage,"===selectedPage")
    const offset= selectedPage *  perPage;
    console.log(offset,"===offset",perPage)
    setCurrentPage(selectedPage)
    setOffset(offset)
  }

  console.log(currentPage, offset)

  return (
    <div className="container mt-5">
      <h1 className="text-primary mb-3">My Blog</h1>
      <Posts posts={posts}/>
      <Pagination
       pageCount={pageCount}
       onPageChange={handlePageClick}
      />
    </div>
  );
};

export default App;
